css:
.print-format {
    min-height: 11in;
    min-width: 8.5in;
}

@media print {
    .print-format {
        min-height: 11in;
        min-width: 8.5in;
    }
}
.table.header > tbody > tr > th {
    font-weight: 600;
}

html:
{{letter_head}}
<div style="padding: 0; margin: 0 auto; width: 100%;">
    <div style="align-items: end; display: flex; flex-wrap: wrap;">
        <div style="flex-basis: 0; flex-grow: 1; min-width: 0; width: 350px;  position: relative; padding: 25px 0 0;">
            <h5 class="m-0">PROFORMA</h5>
            <h4 class="m-0">{{doc.name}}</h4>
        </div>
        <div style="flex-basis: 0; flex-grow: 1; width: auto; max-width: 100%; position: relative; text-align: right;">
            <p style="font-size: 10px; margin-top: 15px; text-transform: uppercase;">PROFORMA<br>
            codigo doc</p>
        </div>
    </div>
</div>
<table class="table table-borderless header" style="margin-top: 15px;">
    <tbody>
        <tr>
            <th>Cliente</th>
            <td>{{ doc.customer_name }}</td>
            <th>Fecha</th>
            <td>{{ doc.get_formatted("transaction_date") }}</td>
        </tr>
        <tr>
            <th>RTN</th>
            <td>{{ doc.tax_id or "" }}</td>
            <th>Valido Hasta</th>
            <td>{{ doc.get_formatted("valid_till") }}</td>
        </tr>
        <tr>
            <th rowspan="2">Dirección</th>
            <td rowspan="2">{{ doc.address_display or "" }}</td>
            <th>Orden de Compra</th>
            <td>{{ doc.po_no or "" }}</td>
        </tr>
        <tr>
            <th></th>
            <td></td>
        </tr>
    </tbody>
</table>
<table class="table my-1">
    <thead class="thead-light">
        <tr>
            <th style="font-size: x-small;">#</th>
            <th style="font-size: x-small;" class="text-uppercase">Descripción</th>
            <th style="font-size: x-small;" class="text-right text-uppercase">Cantidad</th>
            <th style="font-size: x-small;" class="text-right text-uppercase">P. Unitario</th>
            <th style="font-size: x-small;" class="text-right text-uppercase">Total</th>
            <th style="font-size: x-small;" class="text-right text-uppercase">ISV</th>
        </tr>
    </thead>
    <tbody>
        {%- for item in doc.items -%}
        <tr>
            <td style="width: 3%;">{{ item.idx }}</td>
            <td style="width: 37%;">{{ item.item_name }}</td>
            <td style="width: 10%; text-align: right;">{{ item.qty }}</td>
            <td style="width: 15%; text-align: right;">{{ "L {:,.2f}".format(item.rate) }}</td>
            <td style="width: 15%; text-align: right;">{{ "L {:,.2f}".format(item.amount) }}</td>
            <td style="width: 5%; text-align: right;">
                {% set mydict = json.loads(item.item_tax_rate) %}
        		{% if item.item_tax_rate == "{}" %}
        			{% for row in doc.taxes %}
        				{{ row.rate }}.0%
        			{% endfor %}
        		{% else %}
        			{% for obj, value in mydict.items() %}
        			{{ value }}%
        			{% endfor %}
        		{% endif %}
            </td>
        </tr>
        {%- endfor -%}
    </tbody>
</table>

{% set tax_rates_dic = json.loads(doc.taxes[0].item_wise_tax_detail) %}
{% set tax_templates = frappe.get_all('Item Tax Template', fields=['name', 'title']) %}

{% set tax_exento_total = [] %}
{% set tax_exento_tax_total = [] %}
{% set tax_15_tax_total = [] %}
{% set tax_15_total = [] %}
{% set tax_18_tax_total = [] %}
{% set tax_18_total = [] %}

{% for tax_template in tax_templates %}
    {% for item in doc.items %}
        {% if item.item_tax_template == tax_template.name %}
            {% if tax_template.title == "ISV 15%" %}
                {% set _ = tax_15_total.append(item.net_amount) %}
                {% for obj, value in tax_rates_dic.items() %}
                    {% if obj == item.item_code %}
                        {% set _ = tax_15_tax_total.append(value[1]) %}
                    {% endif %}
                {% endfor %}
            {% endif %}
            {% if tax_template.title == "ISV 18%" %}
                {% set _ = tax_18_total.append(item.net_amount) %}
                {% for obj, value in tax_rates_dic.items() %}
                    {% if obj == item.item_code %}
                        {% set _ = tax_18_tax_total.append(value[1]) %}
                    {% endif %}
                {% endfor %}
            {% endif %}
            {% if tax_template.title == "Exento" %}
                {% set _ = tax_exento_total.append(item.net_amount) %}
                {% for obj, value in tax_rates_dic.items() %}
                    {% if obj == item.item_code %}
                        {% set _ = tax_exento_tax_total.append(value[1]) %}
                    {% endif %}
                {% endfor %}
            {% endif %}
        {% endif %}
    {% endfor %}
{% endfor %}
<table class="table table-borderless mt-0">
    <tbody>
        <tr>
            <td></td>
            <td class="totals-label">{{ _("Subtotal") }}</td>
            <td class="totals-amount">	{{ doc.get_formatted("base_net_total") }}</td>
        </tr>
        <tr>
            <td></td>
            <td class="totals-label">{{ _("ISV 15%") }}</td>
            <td class="totals-amount">{{ doc.get_formatted("base_total_taxes_and_charges") }}</td>
        </tr>
        <tr>
            <td>Cantidad en Letras:</td>
            <td class="totals-label"></td>
            <td class="totals-amount"></td>
        </tr>
        <tr>
            <td>{{ frappe.utils.money_in_words(doc.grand_total, " ") }}</td>
            <td class="totals-label">{{ _("Grand Total") }}</td>
            <td class="totals-amount">{{ doc.get_formatted("base_grand_total") }}</td>
        </tr>
        <tr>
            <td>ㅤ</td>
            <td></td>
            <td></td>
        </tr>
        <tr>
            <td></td>
            <td></td>
            <td></td>
        </tr>
        <tr>
            <td>ㅤ</td>
            <td></td>
            <td></td>
        </tr>
        <tr>
            <td></td>
            <td colspan="2" style="border-top: 1px solid #000000; text-align: center;">FIRMA</td>
        </tr>
    </tbody>
</table>
